    private void chkPassActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_chkPassActionPerformed
        // TODO add your handling code here:
        if (chkPass.isSelected()) {
            txtPass.setEditable(false);
        } else {
            txtPass.setEditable(true);
        }
    }//GEN-LAST:event_chkPassActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        boolean isNotIdentified = chkPass.isSelected();
        String role = txtRole.getText();
        String pss = txtPass.getText();
        RoleModify.createRole(role, pss, isNotIdentified);
        txtRole.setText("");
        txtPass.setText("");
        DBAFrame.showRoles();
    }//GEN-LAST:event_jButton1ActionPerformed

